mapper.select:
           select * from(
	select store.roomId,store.store,store.recordDate,ord.count,ord.bookingDays,ord.checkInDate,ord.checkOutDate,store-(case when sum(ord.count) is Null then 0 else sum(ord.count) end) as store1
	from itrip_hotel_temp_store as store left join itrip_hotel_order as ord on store.roomId=ord.roomId and DATE_FORMAT(store.recordDate,'%Y-%m-%d')  BETWEEN DATE_FORMAT(ord.checkInDate,'%Y-%m-%d') and DATE_FORMAT(ord.checkOutDate,'%Y-%m-%d')
	where store.roomId=#{roomId} GROUP BY store.roomId,store.recordDate
)as A where DATE_FORMAT(A.recordDate,'%Y-%m-%d') BETWEEN DATE_FORMAT(#{checkInDate},'%Y-%m-%d') AND DATE_FORMAT(#{checkOutDate},'%Y-%m-%d') order by store1 asc

select id as id, hotelId as hotelId, roomTitle as roomTitle, roomPrice as roomPrice, roomBedTypeId as roomBedTypeId, isHavingBreakfast as isHavingBreakfast, payType as payType, satisfaction as satisfaction, isBook as isBook, isCancel as isCancel, isTimelyResponse as isTimelyResponse from itrip_hotel_room room
and hotelId=#{hotelId}
and roomBedTypeId=#{roomBedTypeId}
and isHavingBreakfast=#{isHavingBreakfast}
and (payType=1 or payType=3)
and (payType=2 or payType=3)
and payType=3
and isBook=#{isBook}
and isCancel=#{isCancel}
and isTimelyResponse=#{isTimelyResponse}
AND
( (SELECT store FROM itrip_hotel_temp_store WHERE roomId = room.id AND date_format(recordDate, '%Y-%m-%d') = date_format(#{item}, '%Y-%m-%d')) IS NULL OR (SELECT store FROM itrip_hotel_temp_store WHERE roomId = room.id AND date_format(recordDate, '%Y-%m-%d') = date_format(#{item}, '%Y-%m-%d')) > 0 )
order by creationDate desc
limit #{beginPos},#{pageSize}

select count(*) from itrip_hotel_room


            and id=#{id}


            and hotelId=#{hotelId}


            and roomTitle=#{roomTitle}


            and roomPrice=#{roomPrice}


            and roomBedTypeId=#{roomBedTypeId}


            and isHavingBreakfast=#{isHavingBreakfast}


            and payType=#{payType}


            and satisfaction=#{satisfaction}


            and isBook=#{isBook}


            and isCancel=#{isCancel}


            and isTimelyResponse=#{isTimelyResponse}


            and creationDate=#{creationDate}


            and createdBy=#{createdBy}


            and modifyDate=#{modifyDate}


            and modifiedBy=#{modifiedBy}
